'use client';

import Image from 'next/image';
import { useState } from 'react';

interface OptimizedImageProps {
  src: string;
  alt: string;
  width?: number;
  height?: number;
  priority?: boolean;
  className?: string;
  sizes?: string;
  fill?: boolean;
  style?: React.CSSProperties;
  onLoad?: () => void;
}

export function OptimizedImage({
  src,
  alt,
  width,
  height,
  priority = false,
  className = '',
  sizes = '100vw',
  fill = false,
  style,
  onLoad,
}: OptimizedImageProps) {
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(false);

  const handleLoad = () => {
    setIsLoading(false);
    onLoad?.();
  };

  const handleError = () => {
    setError(true);
    setIsLoading(false);
  };

  if (error) {
    return (
      <div 
        className={`bg-gray-200 flex items-center justify-center ${className}`}
        style={fill ? { position: 'absolute', inset: 0 } : { width, height }}
      >
        <span className="text-gray-400 text-sm">Image unavailable</span>
      </div>
    );
  }

  return (
    <>
      {fill ? (
        <Image
          src={src}
          alt={alt}
          fill
          sizes={sizes}
          priority={priority}
          className={`${className} ${isLoading ? 'blur-sm' : 'blur-0'} transition-all duration-300`}
          style={style}
          onLoad={handleLoad}
          onError={handleError}
          quality={85}
          placeholder="blur"
          blurDataURL="data:image/jpeg;base64,"
        />
      ) : (
        <Image
          src={src}
          alt={alt}
          width={width || 500}
          height={height || 500}
          sizes={sizes}
          priority={priority}
          className={`${className} ${isLoading ? 'blur-sm' : 'blur-0'} transition-all duration-300`}
          style={style}
          onLoad={handleLoad}
          onError={handleError}
          quality={85}
          placeholder="blur"
          blurDataURL="data:image/jpeg;base64,"
        />
      )}
    </>
  );
}